        ArrayList<String> options6 = new ArrayList<>(Arrays.asList("1 Hz", "10 Hz", "100 Hz", "1000 Hz"));
        MCQ mcq6 = new MCQ("What is the frequency of a wave which has a period of 0.1 s?", options6, 1, "data:image/png;base64,");

        ArrayList<String> options7 = new ArrayList<>(Arrays.asList("1 m/s", "10 m/s", "100 m/s", "1000 m/s"));
