        ArrayList<String> options8 = new ArrayList<>(Arrays.asList("1 m/s", "10 m/s", "100 m/s", "1000 m/s"));
        MCQ mcq8 = new MCQ("What is the speed of light in vacuum?", options8, 3, "data:image/jpeg;base64,");

